create database WEB_TRUYEN_TRANH
USE WEB_TRUYEN_TRANH


CREATE TABLE USERS(
	 ID INT IDENTITY(1,1) NOT NULL UNIQUE,
	 FULLNAME NVARCHAR(50) NOT NULL,
	 EMAIL VARCHAR(50) NOT NULL,
	 PHONE VARCHAR(10) NOT NULL,
	 USERNAME VARCHAR(50),
	 PASSWORD VARCHAR(50) NOT NULL,
	 ROLE_USER INT DEFAULT 0,
	 PRIMARY KEY (USERNAME)
)



CREATE TABLE COMICS(
	ID INT IDENTITY(1,1),
	TITLE NVARCHAR(100) NOT NULL,
	DESCRIPTION NVARCHAR(500) NOT NULL,
	THUMBNAIL VARCHAR(200) NOT NULL,
	AUTHOR NVARCHAR(50) NOT NULL,
	STATUS NVARCHAR(50) NOT NULL,
	VIEWS INT DEFAULT 0,
	PRIMARY KEY (ID)
)


CREATE TABLE CHAPTERS(
	COMICID INT NOT NULL,
	CHAPTER INT,
	IMGID INT,
	CHAPTER_CONTENT_URL VARCHAR(100) NOT NULL,
	CREATED DATETIME DEFAULT GETDATE(),
	PRIMARY KEY (COMICID,CHAPTER, IMGID),
	CONSTRAINT FK_CHAPTER_COMICID_COMIC FOREIGN KEY (COMICID) REFERENCES COMICS(ID)
)
select * from chapters

CREATE TABLE COMMENTS (
	ID INT IDENTITY(1,1),
	COMICID INT NOT NULL,
	USERID INT NOT NULL,
	COMMENT NVARCHAR(500) NOT NULL,
	CREATED DATETIME DEFAULT GETDATE(),
	PRIMARY KEY (ID),
	CONSTRAINT FK_COMMENT_COMICID_COMIC FOREIGN KEY (COMICID) REFERENCES COMICS(ID),
	CONSTRAINT FK_COMMENT_USERID_COMIC FOREIGN KEY (USERID) REFERENCES USERS(ID),
)

CREATE TABLE TAG_DESCRIPTION
(
	TAG VARCHAR(50) NOT NULL ,
	TAG_NAME NVARCHAR(50)NOT NULL ,
	DESCRIPTIONTAG NVARCHAR(500)NOT NULL ,
	PRIMARY KEY (TAG)
	
)

CREATE TABLE TAGS(
	TAG VARCHAR(50),
	COMICID INT,
	PRIMARY KEY (TAG, COMICID),
	CONSTRAINT FK_TAG_COMICID_COMIC FOREIGN KEY (COMICID) REFERENCES COMICS(ID),
	CONSTRAINT FK_TAG_TAG_DESCRIPTION FOREIGN KEY (TAG) REFERENCES TAG_DESCRIPTION(TAG),
)





CREATE TABLE INTERACTIONS(
	COMICID INT,
	USERID INT,
	FAVOR BIT DEFAULT 0,
	FOLLOW BIT DEFAULT 0,
	PRIMARY KEY (COMICID, USERID),
	CONSTRAINT FK_INTERACTION_COMICID_COMIC FOREIGN KEY (COMICID) REFERENCES COMICS(ID),
	CONSTRAINT FK_INTERACTION_USERID_COMIC FOREIGN KEY (USERID) REFERENCES USERS(ID),
)


CREATE TABLE HISTORIES(
	USERID INT,
	COMICID INT,
	CREATED DATETIME DEFAULT GETDATE(),
	ISREAD BIT DEFAULT 1,
	PRIMARY KEY (COMICID, USERID),
	CONSTRAINT FK_HISTORY_COMICID_COMIC FOREIGN KEY (COMICID) REFERENCES COMICS(ID),
	CONSTRAINT FK_HISTORY_USERID_COMIC FOREIGN KEY (USERID) REFERENCES USERS(ID),
)

CREATE TRIGGER trg_before_del_USERS ON USERS INSTEAD OF DELETE 
as
begin
	declare @deleteid int
	select @deleteid =  ID from deleted
	
	delete from comments where comments.USERID=@deleteid
	
	delete from interactions where interactions.USERID=@deleteid
	delete from histories where histories.USERID=@deleteid
	delete from USERS where id = @deleteid 
end



CREATE TRIGGER trg_before_del_comics ON COMICS INSTEAD OF DELETE 
as
begin
	declare @deleteid int
	select @deleteid =  ID from deleted
	delete from chapters where chapters.comicid=@deleteid
	delete from comments where comments.comicid=@deleteid
	delete from tags where tags.comicid=@deleteid
	delete from interactions where interactions.comicid=@deleteid
	delete from histories where histories.comicid=@deleteid
	delete from comics where id = @deleteid 
end

CREATE TRIGGER trg_before_del_TAG_DESCRIPTION ON TAG_DESCRIPTION INSTEAD OF DELETE 
as
begin
	declare @deletetag VARCHAR(50)
	select @deletetag =  TAG from deleted
	
	delete from TAGS where TAGS.TAG=@deletetag
	

	delete from TAG_DESCRIPTION where TAG = @deletetag 
end


create trigger update_like on INTERACTIONS after update 
as
begin
	declare @tamCOMICID int, @tamUSERID int, @oldFavor bit, @oldDisfavor bit, @newFavor bit, @newDisfavor bit
	select @tamCOMICID = COMICID, @tamUSERID = USERID from inserteD 
	select @oldFavor = favor, @oldDisfavor = disfavor from deleted
	select @newFavor = favor, @newDisfavor = disfavor from inserted
	
	if(@oldFavor = 0 AND @newFavor = 1)
		begin
			update INTERACTIONS
			set INTERACTIONS.DISFAVOR = 0 WHERE (INTERACTIONS.COMICID = @tamCOMICID AND INTERACTIONS.USERID = @tamUSERID)
		end
	if(@oldDisfavor = 0 AND @newDisfavor = 1)
		begin
			update INTERACTIONS
			set INTERACTIONS.FAVOR = 0 WHERE (INTERACTIONS.COMICID = @tamCOMICID AND INTERACTIONS.USERID = @tamUSERID)
		end
	
end

