insert into TAG_DESCRIPTION values('chuyen-sinh',N'Chuyển sinh',N'Thể loại này là những câu chuyện về người ở một thế giới này xuyên đến một thế giới khác, có thể là thế giới mang phong cách trung cổ với kiếm sĩ và ma thuật, hay thế giới trong game, hoặc có thể là bạn chết ở nơi này và được chuyển sinh đến nơi khác')
insert into TAG_DESCRIPTION values('drama',N'Drama',N'Thể loại mang đến cho người xem những cảm xúc khác nhau: buồn bã, căng thẳng thậm chí là bi phẫn')
insert into TAG_DESCRIPTION values('gia-tuong',N'Giả tưởng',N'Bao gồm những chuyện khoa học viễn tưởng, đa phần chúng xoay quanh nhiều hiện tượng mà liên quan tới khoa học, công nghệ, tuy vậy thường thì những câu chuyện đó không gắn bó chặt chẽ với các thành tựu khoa học hiện thời, mà là do con người tưởng tượng ra.')
insert into TAG_DESCRIPTION values('hai-huoc',N'Hài hước',N'Thể loại có nội dung trong sáng và cảm động, thường có các tình tiết gây cười, các xung đột nhẹ nhàng')
insert into TAG_DESCRIPTION values('hanh-dong',N'Hành động',N'Thể loại này thường có nội dung về đánh nhau, bạo lực, hỗn loạn, với diễn biến nhanh')
insert into TAG_DESCRIPTION values('hoc-duong',N'Học đường',N'Trong thể loại này, ngữ cảnh diễn biến câu chuyện chủ yếu ở trường học')
insert into TAG_DESCRIPTION values('kinh-di',N'Kinh dị',N'Thể loại có nội dung rùng rợn, nghe cái tên là bạn đã hiểu thể loại này có nội dung thế nào. Nó làm cho bạn kinh hãi, khiếp sợ, ghê tởm, run rẩy, có thể gây sock - một thể loại không dành cho người yếu tim')
insert into TAG_DESCRIPTION values('ngon-tinh',N'Ngôn tình',N'Truyện thuộc kiểu lãng mạn, kể về những sự kiện vui buồn trong tình yêu của nhân vật chính.')
insert into TAG_DESCRIPTION values('phieu-luu',N'Phiêu lưu',N'Thể loại phiêu lưu, mạo hiểm, thường là hành trình của các nhân vật')
insert into TAG_DESCRIPTION values('shounen',N'Shounen',N'Đối tượng hướng tới của thể loại này là phái nam. Nội dung của những bộ manga này thường liên quan đến đánh nhau và/hoặc bạo lực (ở mức bình thường, không thái quá)')
insert into TAG_DESCRIPTION values('truyen-mau',N'Truyện màu',N'Tổng hợp truyện tranh màu, rõ, đẹp')
insert into TAG_DESCRIPTION values('xuyen-khong',N'Xuyên không',N'Xuyên Không là thể loại nhân vật chính vì một lý do nào đó mà bị đưa đến sinh sống ở một không gian hay một khoảng thời gian khác. Nhân vật chính có thể trực tiếp xuyên qua bằng thân xác mình hoặc sống lại bằng thân xác người khác.')
insert into TAG_DESCRIPTION values('all',N'Tất cả',N'Tất cả thể loại truyện tranh')
